import React, { Component } from "react";
//import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      json: {},
    };
  }

  handleQueryChange(){
    this.setState({ })
  }

  handleSubmit(){
    //query the api with our params
  }
  componentDidMount() {
    this.fetchProducts();
  }

  async fetchProducts() {
    const resp = await fetch("http://localhost:4000/primark?term=green%20hat");
    const json = await resp.json();

    console.log(json);
    this.setState({ json });
  }

  render() {
    let products = null;
    if (this.state.json.products) {
      products = this.state.json.products.map((product) => {
        return (
          <div key={product.code}>
            <div key={product.code}>{product.name}</div>
            <img src={product.images[0].url} />
          </div>
        );
      });
    }

    return <div className="App">
      <form onSubmit={this.handleSubmit}>
      <label>Amount: </label>
      <input type="number"
      value={this.state.amount}
      onChange={this.handleAmountChange}
      />
      </form>
      {products}</div>;
  }
}

export default App;
